mapper.select:
select * from ( select u.id,i.name,u.account,u.createDate,u.losingEffect ,p.location image ,null roles,null perms,d.name deptname, case when u.`disable` =1 then 1 else case when DATEDIFF(u.losingEffect,NOW()) >0 then 0 ELSE 2 END END `status`, IFNULL((select max(case when role_id=#{dto.roleid,jdbcType=CHAR} then 1 else 0 end) from t_user_role where user_id=u.id),0) rolecount from t_user_shop s left join t_user u on u.leader_id=s.user_id left join t_userinfo i on i.id=u.id left join t_picture p on p.id=i.picture left join t_sys_dept d on d.id=u.deptid where s.shop_id = #{dto.shopid,jdbcType=CHAR} and u.logicDelete=false
and i.name like #{dto.name,jdbcType=CHAR}
and u.account like #{dto.account,jdbcType=CHAR}
) v where 1=1
and rolecount>0
and status= #{dto.status,jdbcType=CHAR}
